import React from 'react';
import Data from '../../Data';
import './PageStyles.css';
const Home = () => {
  return (
    <section className='home'>
        {Data.map((item) =>(
            <div className='wrapper'>
                    <div className='image'>
                        <img src={item.image} />
                    </div>
                    <div className='content'>
                        <h1>GET 30% ON ALL YOUR SALES</h1>
                        <button>Shop Now...!!!</button>
                    </div>
            </div>
         ))}
    </section>
  );
}
export default Home;
